package javabsics;

public class StringToLower {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String str = "LEARNING JAVA";
		
		String str1= "learning automation";
		
		String value =str.toLowerCase();
		String value1=str.toUpperCase();
		
	
		
		System.out.println("To lower case : " +value);
		System.out.println("To upper case : " +value1);
		

	}

}
